import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TextInput, View, Button, TouchableOpacity, ScrollView } from 'react-native';


export default function login({ navigation })  {

  const [username, setUsername] = useState('');
  const [age, setAge] = useState('');
  const [phone, setPhone] = useState('');
  const [password, setPassword] = useState('');

  const [loading, setLoading] = useState(false);

  const handlePress = () => {
    setLoading(true);

    setTimeout(() => {
      setLoading(false);
    }, 2000); // Simulating a 2-second delay

    console.log('Submitted:', { username, age, phone, password });
    navigation.push('Home');
  };

  return (
    <View style={styles.container}>
      <ScrollView style={styles.scrollStyle}>

        <View style={styles.innerContainer}>

          <Text style={styles.headerText}>
            Welcome to Sander
          </Text>
          <TextInput
            style={styles.input}
            placeholder="Username"
            value={username}
            onChangeText={(text) => setUsername(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Age"
            keyboardType="numeric"
            value={age}
            onChangeText={(text) => setAge(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Phone"
            keyboardType='phone-pad'
            value={phone}
            onChangeText={(text) => setPhone(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Password"
            secureTextEntry
            value={password}
            onChangeText={(text) => setPassword(text)}
          />
          <TextInput
            style={styles.input}
            placeholder="Password"
            secureTextEntry
            value={password}
            onChangeText={(text) => setPassword(text)}
          />

          <TouchableOpacity
            style={styles.buttonStyles}
            onPress={handlePress}
            disabled={loading} >
            <Text style={styles.buttonText}>{loading ? 'Loading...' : 'Submit'}</Text>
          </TouchableOpacity>

        </View>

      </ScrollView>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    borderColor: 'white',
    borderWidth: 0,
  },
  innerContainer: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    borderColor: 'white',
    borderWidth: 0,
  },

  scrollStyle: {
    backgroundColor: 'red',
    marginLeft: 15,
    marginRight: 15,
    marginTop: 5,
    marginBottom: 5,
  },

  input: {
    height: 50,
    width: '90%',
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 10,
    paddingHorizontal: 10,
    borderRadius: 10,
  },

  headerText: {
    fontWeight: 'bold',
    color: '#008080',
    marginBottom: 20,
    marginTop: 20,
    fontSize: 24,
    paddingHorizontal: 10,
    textAlign: 'center',
  },

  buttonStyles: {
    marginBottom: 20,
    backgroundColor: '#fff',
    borderColor: '#008080',
    borderWidth: 2,
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 5,
    opacity: 0.5,
  },
  buttonText: {
    color: '#008080',
    fontSize: 16,
    fontWeight: 'bold',
  },

});
