import React from 'react';
import { StyleSheet, Text, View, Button, TouchableOpacity } from 'react-native';
import PagerView from 'react-native-pager-view';
import { LinearGradient } from 'expo-linear-gradient';
import { StatusBar } from 'expo-status-bar';


export default function Landing({ navigation }) {
  return (
    <View style={styles.container}>

      <StatusBar style="auto" />

      <PagerView 
        style={styles.container} 
        initialPage={0} 
        orientation={'horizontal'} >


        <LinearGradient  colors={['#15f8b6',  '#276250' ]}  style={styles.page1} key='1'>
        <Text style={styles.defaultText}> First page{'\n'} Swipe ➡️ </Text>
        </LinearGradient>


        <LinearGradient  colors={['#276250',  '#15f8b6' ]}  style={styles.page2} key='2'>
        <Text style={styles.defaultText}> Second page</Text>
        </LinearGradient>

        <LinearGradient  colors={['#f85715',  '#c11732' ]}  style={styles.page3} key='3'>
        <Text>Third page</Text>

          <TouchableOpacity
            style={styles.buttonStyles}
            onPress={() => navigation.navigate('Entrance')}>
            <Text style={styles.buttonText}>Continue</Text>
          </TouchableOpacity>
        </LinearGradient>

      </PagerView>

      <TouchableOpacity
        style={styles.skipStyles}
        onPress={() => navigation.navigate('Entrance')}>
        <Text style={styles.skipbuttonText}>Skip</Text>
      </TouchableOpacity>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },

  buttonStyles: {
    backgroundColor: '#008080',
    borderColor: '#fff',
    borderWidth: 2,
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 20,
    opacity: 0.9,
    height: 40,
    position: 'absolute',
    bottom: 0,
    right: 0,
    margin: 30,
  },

  skipStyles: {
    backgroundColor: '#fff',
    borderColor: '#008080',
    borderWidth: 2,
    paddingHorizontal: 20,
    paddingVertical: 10,
    borderRadius: 20,
    opacity: 0.9,
    height: 40,
    position: 'absolute',
    bottom: 0,
    right: 70,
    margin: 30,
  },

  skipbuttonText: {
    color: '#008080',
    fontSize: 11,
    fontWeight: 'bold',
  },
  buttonText: {
    color: '#fff',
    fontSize: 11,
    fontWeight: 'bold',
  },

  page1: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'tomato',
  },
  page2: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'teal'
  },
  page3: {
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'pink'
  },
  defaultText: {
    color: 'white',
  }
});


