import React from 'react';
import { SafeAreaProvider } from 'react-native-safe-area-context';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { StyleSheet, View, Image, TouchableOpacity} from 'react-native';
import FontAwesome from '@expo/vector-icons/FontAwesome';
import { StatusBar } from 'react-native';

import HomeScreen from './HomeScreen';
import LandingScreen from './screens/Landing';
import LoginScreen from './screens/Login';
import EntranceScreen from './screens/Entrance';
import NotificationScreen from './screens/Notification';
import ScanScreen from './screens/Scan';



const Stack = createStackNavigator();

export default function App() {
  return (
    <SafeAreaProvider>
      <StatusBar barStyle={'auto'} />

      <NavigationContainer>
        <Stack.Navigator initialRouteName="Landing"
          screenOptions={({ navigation }) => ({ 
            headerStyle: {
              backgroundColor: '#fff',
            },
            headerTintColor: '#008080',
            headerTitleStyle: {
              fontWeight: 'bold',
              fontSize: 18
            },
            headerMode: 'screen',
            headerTitleAlign: 'center', 
            headerRight: () => (
              <View style={styles.iconBar}>
                <FontAwesome style={styles.icos} size={20} name="bell-o" onPress={() => navigation.navigate('Notifications')} />
                <FontAwesome style={styles.icos} size={20} name="qrcode" onPress={() => navigation.navigate('Scans')} />
              </View>
            ),

            headerBackAllowFontScaling: false,
            headerTitleAllowFontScaling: false,
          })}
        >

        <Stack.Screen
          name="Landing"
          component={LandingScreen}
          options={{ 
            title: 'Skai Hub', 
            headerShown: false }}
        />
        <Stack.Screen
          name="Entrance"
          component={EntranceScreen}
          options={{ 
            headerLeft: () => (
              <TouchableOpacity onPress={() => {}}>
                <Image
                source={require('./assets/user1.jpg')}
                style={{ width: 40, height: 40, marginLeft: 20, borderRadius: 50, borderWidth: 2, borderColor: 'ghostwhite' }}
              />
              </TouchableOpacity>
            ),
            title: 'Entrance Host', 
            headerShown: true, 
            headerShadowVisible: false,
           }}
        />

        <Stack.Screen
          name="Notifications"
          component={NotificationScreen}
          options={{ 
            title: 'Notification Area', 
            headerShown: true, 
            headerShadowVisible: false,
           }}
        />

        <Stack.Screen
          name="Scans"
          component={ScanScreen}
          options={{ 
            title: 'Scan QrCode', 
            headerShown: true, 
            headerShadowVisible: false,
           }}
        />

        <Stack.Screen
          name="Login"
          component={LoginScreen}
          options={{ 
            headerLeft: () => (
              <TouchableOpacity onPress={() => {}}>
                <Image
                source={require('./assets/user1.jpg')}
                style={{ width: 40, height: 40, marginLeft: 20, borderRadius: 50, borderWidth: 2, borderColor: 'ghostwhite' }}
              />
              </TouchableOpacity>
            ),
            title: 'Authentication', 
            cardShadowEnabled: false, 
            headerShadowVisible: false ,
            headerShown: true, 
            // headerLeft: null,
          }}
        />



        <Stack.Screen name="Home" component={HomeScreen}  options={{ headerShown: false}} />

        </Stack.Navigator>
      </NavigationContainer>

    </SafeAreaProvider>

  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  iconBar: {
    flexDirection: 'row',
    alignItems: 'center',
  },

  icos: {
    marginRight: 20,
    color: 'silver'
  }


});


